import React, { useState } from "react";
import "./main.css";
import axios from "axios";
import Swal from 'sweetalert2';

const Main = () => {
  const [peticionReporte, setPeticionReporte] = useState("");
  const AuthorizationHeader = '3c96d6117865dae9d77dbb517c3972907e6d245c'; // Clave API de Redmine

  const ClienteRedmine = axios.create({
    baseURL: 'http://localhost:3000',
    headers: {
      'Content-Type': 'application/json',
      'X-Redmine-API-Key': AuthorizationHeader
    }
  });

  const envioPeticion_Reporte = async () => {
    if (peticionReporte.length === 0 || peticionReporte === "") {
      Swal.fire({
        icon: 'warning',
        title: 'Advertencia',
        text: 'No se ingresó ningún reporte. Vuelva a intentar.',
      });
    } else {
      try {
        const cuerpoPeticion = {
          issue: {
            project_id: "projecttickets", // ID del proyecto en Redmine
            tracker_id: 1, // ID del tracker en Redmine
            status_id: 1,  // ID del estado en Redmine
            priority_id: 4, // ID de la prioridad en Redmine
            subject: peticionReporte,
            description: "Prueba desde aplicación React Grupo 4."
          }
        };

        const respuesta = await ClienteRedmine.post("/issues.json", cuerpoPeticion);
        if (respuesta.status === 201) {
          Swal.fire({
            icon: 'success',
            title: 'Creación Exitosa',
            text: "Se creó un reporte sobre el problema exitosamente."
          }).then(() => setPeticionReporte(""));
        } else {
          Swal.fire({
            icon: 'error',
            title: 'Error',
            text: 'Ocurrió un error al crear el reporte.'
          });
        }
        return true;
      } catch (error) {
        console.log(error);
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'Ocurrió un error al enviar la petición.'
        });
      }
    }
  }

  return (
    <div className="principal">
      <div className="cajaCentral">
        <h1 className="text">Reportar problema</h1>
        <div>
          <textarea
            type="text"
            className="entradaPeticion"
            placeholder="Ingrese reporte sobre problema"
            onChange={(event) => setPeticionReporte(event.target.value)}
            value={peticionReporte}
          />

        </div>
        <button className="Sendbt" type="button" onClick={envioPeticion_Reporte}>Enviar</button>
      </div>
    </div>
  )
}

export default Main;
